let carousel = document.getElementById('results-carousel11');
let currentPosition = 0;  // 当前显示的轮播项
let mediaContainers = document.querySelectorAll('.media-container');  // 获取所有 .media-container 元素
let totalContainers = mediaContainers.length;  // 获取容器的总数

// 设置轮播的滚动速度（单位：毫秒）
let scrollInterval = 3000;  // 每3秒切换一次

function moveCarousel() {
  // 计算轮播容器的移动距离
  currentPosition++;
  if (currentPosition >= totalContainers) {
    currentPosition = 0;  // 如果已经到达最后一个，回到第一个
  }

  // 使用transform属性来滑动轮播容器
  carousel.style.transform = `translateX(-${currentPosition * 50}%)`;  // 每次移动50%的宽度
}

// 每隔一段时间触发轮播
setInterval(moveCarousel, scrollInterval);
